import React, { Component } from "react";
import TamaNFT from "./contracts/TamaNFT.json";
import getWeb3 from "./getWeb3";

import "./App.css";

class App extends Component {
  state = { storageValue: 0, web3: null, accounts: null, contract: null, balanceOf: 0, totalSupply: 0, maxMint: 0, price: 0 };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      let account = await web3.eth.getAccounts();
      account = account[0].toLowerCase();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = TamaNFT.networks[networkId];
      const instance = new web3.eth.Contract(
        TamaNFT.abi,
        deployedNetwork && deployedNetwork.address,
      );

      let balanceOf = await instance.methods.balanceOf(account).call();
      let totalSupply = await instance.methods.totalSupply().call();
      let maxMint = await instance.methods.MAX_MINT().call();
      let price = await instance.methods.price().call();

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, account, contract: instance, balanceOf, totalSupply, maxMint, price });
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  mint = async () => {
    let mintAmount = document.getElementById("mint-amount").value;
    await this.state.contract.methods.mint(mintAmount).send({from: this.state.account, value: this.state.price*mintAmount});
    window.location.reload()
  }

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div>
      <nav className="navbar navbar-dark flex-md-nowrap p-0 shadow">
        <a
          className="navbar-brand col-sm-3 mr-0"
          href="http://www.dappuniversity.com/bootcamp"
          target="_blank"
          rel="noopener noreferrer"
        >
          <img class="App-logo" src="TAMA Monster Club - LOGO.png" alt="app-logo"/>
        </a>
      </nav>
      <div className="container-fluid mt-5">
        <div id="uni">
          <img id="first-image" src="TAMA-Twitter-Header.jpg"></img>
          <main id="m" className="text-center">
            <div class="container">
              <div class="row">
                <div class="column"><strong>THE MONSTERS ARE ALIVE</strong></div>
                <div class="column"><p id="text">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p></div>
              </div>
              <h6 id="mintA">MINT A MONSTER</h6>
              <h6 id="mintedFar">{this.state.totalSupply} / {this.state.maxMint} NFTs minted so far.</h6>
              <p>You have minted {this.state.balanceOf} NFTs</p>
              <p>You can only mint 20 NFTs maximum.</p>
              <p>Price is {this.state.price/10**18} ether + gas</p>
              <input id="mint-amount" class="change" placeholder="Enter Number of NFTs to mint"></input>
              <button id="mint" class="change-button" onClick={() => this.mint()}>Mint</button>
              <a id="link" href={`https://rinkeby.etherscan.io/address/${this.state.contract._address}`} target="_blank" rel="noopener noreferrer">Contract Address</a>
              <br/>
              <a id="link" href="https://testnets.opensea.io/collection/tama-monster-club-v2" target="_blank" rel="noopener noreferrer">OpenSea Collection</a>


            </div>
          </main>
        </div>
      </div>
    </div>
    );
  }
}

export default App;
