import React, { Component } from "react";
import TamaNFT from "./contracts/TamaNFT.json";
import getWeb3 from "./getWeb3";

import "./App.css";

class App extends Component {
  state = { storageValue: 0, web3: null, accounts: null, contract: null };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      let account = await window.ethereum.request({ method: 'eth_accounts' });
      account = account[0].toLowerCase();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = TamaNFT.networks[networkId];
      const instance = new web3.eth.Contract(
        TamaNFT.abi,
        deployedNetwork && deployedNetwork.address,
      );

      let balanceOf = await instance.methods.balanceOf(account).call();

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, account, contract: instance, balanceOf });
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  mint = async () => {
    let mintAmount = document.getElementById("mint-amount").value;
    await this.state.contract.methods.mint(mintAmount).send({from: this.state.account});
  }

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div>
      <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
        <a
          className="navbar-brand col-sm-3 col-md-2 mr-0"
          href="http://www.dappuniversity.com/bootcamp"
          target="_blank"
          rel="noopener noreferrer"
        >
          Tama NFTs Minting
        </a>
      </nav>
      <div className="container-fluid mt-5">
        <div className="row">
          <main role="main" className="col-lg-12 d-flex text-center">
            <div class="container">
              <p>You can only mint 20 NFTs maximum.</p>
              <input id="mint-amount" class="change" placeholder="Enter Number of NFTs to mint"></input>
              <button id="mint" class="change-button" onClick={() => this.mint()}>Mint</button>
            </div>
          </main>
        </div>
      </div>
    </div>
    );
  }
}

export default App;
